// Create a class called Book with title, author, and year in the constructor
// Add a method called getSummary that returns a summary string

// Create an instance of Book and log the result of getSummary

example:
class Book {
  constructor(title, author, year) {
    this.title = title;
    this.author = author;
    this.year = year;
  }

  getSummary() {
    return `The book '${this.title}' was written by ${this.author} in ${this.year}.`;
  }
}

const b1 = new Book('1984', 'George Orwell', 1949);
console.log(b1.getSummary());

placeholder:
// Define a class called Student with name and grades
// Add a method getAverage that returns the average grade

// Create two students and store them in an array
// Loop over the students, log each average, and find the top student

example:
class Student {
  constructor(name, grades) {
    this.name = name;
    this.grades = grades;
  }

  getAverage() {
    return this.grades.reduce((a, b) => a + b) / this.grades.length;
  }
}

function createStudent(name, grades) {
  return new Student(name, grades);
}

const students = [
  createStudent('Alice', [90, 85, 95]),
  createStudent('Bob', [70, 60, 65])
];

for (let s of students) {
  const avg = s.getAverage();
  console.log(`${s.name} average: ${avg}`);
  if (avg >= 90) console.log('Excellent');
  else if (avg >= 75) console.log('Good');
  else console.log('Needs Improvement');
}

function getTopStudent(students) {
  return students.reduce((top, current) => current.getAverage() > top.getAverage() ? current : top);
}

console.log('Top student: ' + getTopStudent(students).name);
